import React,{useEffect} from 'react'
import Typography from '@mui/material/Typography';
import { useDispatch, useSelector } from "react-redux";
import {setsensordata} from "../features/sensordata"
import io from "socket.io-client";
import { Gauge} from '@mui/x-charts/Gauge';


const socket=io.connect("http://localhost:4000");

function Sensorstatus() {

  const dispatch = useDispatch();
  const sensordata = useSelector(state => state.sensordata.value);
  
  useEffect(()=>{
    socket.on("Sensordata",(data)=>{
        dispatch(setsensordata(data)); 
    });
  });
  
  return (
    <div className="flex justify-center items-center h-screen">
      <Gauge width={100} height={100} value={60} startAngle={-90} endAngle={90} />
      <Typography variant="body2" color="text.secondary">
        Overhead Tank Float Switch     : {sensordata.OHT_Float}
      </Typography>
      <Gauge width={100} height={100} value={60} startAngle={-90} endAngle={90} />
      <Typography variant="body2" color="text.secondary">
        Underground Tank Float Switch  : {sensordata.UGT_Float}
      </Typography>
    </div>
  )
}

export default Sensorstatus;
