import * as React from 'react';
import dayjs from 'dayjs';
import Stack from '@mui/material/Stack';
import Typography from '@mui/material/Typography';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import {settime} from '../features/time';
import {useDispatch,useSelector} from 'react-redux';

export default function Timer() {

  const dispatch = useDispatch();  
  const timedata = useSelector(state => state.time.value);

  const Start_handlechange = (Event) =>{
    const start_time = Event.target.value;
    dispatch(settime({starttime:start_time}));
  }

  const End_handlechange =(Event) =>{
    const end_time = Event.target.value;
    dispatch(settime({endtime:end_time}));
  }

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Stack spacing={2} sx={{ minWidth: 305 }}>
      <Typography variant="subtitle1">
        Start Time
      </Typography>
        <TimePicker
          value={timedata.starttime}
          onChange={Start_handlechange}
          referenceDate={dayjs('2022-04-17')}
        />
      <Typography variant="subtitle1">
        End Time
      </Typography>
       <TimePicker
          value={timedata.endtime}
          onChange={End_handlechange}
          referenceDate={dayjs('2022-04-17')}
        />

      </Stack>
    </LocalizationProvider>
  );
}
