import * as React from 'react';
import dayjs from 'dayjs';
import Stack from '@mui/material/Stack';
import Typography from '@mui/material/Typography';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { Button, FormControl, FormHelperText } from '@mui/material';
import { settime } from '../features/time';
import { settimer } from '../features/timerswitch';
import { useDispatch, useSelector } from 'react-redux';
import io from "socket.io-client";
import FormControlLabel from '@mui/material/FormControlLabel';
import Switch from '@mui/material/Switch';

const socket=io.connect("http://localhost:4000");

export default function Timer() {
  const dispatch = useDispatch();
  const timedata = useSelector(state => state.time.value);
  const timerdata = useSelector(state => state.timer.value);

  let startTemp = timedata.starttime ? dayjs(timedata.starttime, 'HH:mm') : null;
  let endTemp = timedata.endtime ? dayjs(timedata.endtime, 'HH:mm') : null;

  const handleSetTime = () => {
    dispatch(settime({
      starttime: startTemp ? startTemp.format('HH:mm') : null,
      endtime: endTemp ? endTemp.format('HH:mm') : null,
    }));
    socket.emit("Timerdata",{
        starttime:startTemp.format('HH:mm'),
        endtime:endTemp.format('HH:mm')
    });
  };

  const handleStartChange = (newValue) => {
    startTemp = newValue;
  };

  const handleEndChange = (newValue) => {
    endTemp = newValue;
  };
  
  const handleChange = (Event)=>{
    const isChecked = Event.target.checked;
    const state = isChecked ? "ENABLED" : "DISABLED";
    dispatch(settimer({
        mode:isChecked,
        modestate:state
    }))
  }

  console.log(timedata);

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <Stack spacing={2} sx={{ minWidth: 305 }}>
      <FormControlLabel
        control={
          <Switch
            checked={timerdata.mode}
            onChange={handleChange}
          />
        }
        label={timerdata.modestate}
      />
        <FormControl>
          <Typography variant="subtitle1">Start Time</Typography>
          <TimePicker
            value={startTemp}
            onChange={handleStartChange}
            referenceDate={dayjs('2022-04-17')}
          />
        </FormControl>
        <FormControl>
          <Typography variant="subtitle1">End Time</Typography>
          <TimePicker
            value={endTemp}
            onChange={handleEndChange}
            referenceDate={dayjs('2022-04-17')}
          />
        </FormControl>
        <Button variant="contained" onClick={handleSetTime}>
          Set Time
        </Button>
        <FormHelperText>Click "Set Time" to save the changes</FormHelperText>
      </Stack>
    </LocalizationProvider>
  );
}
