import React,{useEffect} from 'react'
import Typography from '@mui/material/Typography';
import { useDispatch, useSelector } from "react-redux";
import {setsensordata} from "../features/sensordata"
import io from "socket.io-client";


const socket=io.connect("http://localhost:4000");

function Sensorstatus() {

  const dispatch = useDispatch();
  const sensordata = useSelector(state => state.sensordata.value);
  const pumpstatus = useSelector(state => state.switchstate.value);
  
  useEffect(()=>{
    socket.on("Sensordata",(data)=>{
        dispatch(setsensordata(data)); 
    });
  });
  
  return (
    <div>
      <Typography variant="body2">
        Overhead Tank Float Switch  is {sensordata.OHT_Float}
      </Typography>
      <Typography variant="body2">
        Underground Tank Float Switch  is {sensordata.UGT_Float}
      </Typography>
      <Typography variant="body2">
        Submerisible Pump is {pumpstatus}
      </Typography>
    </div>
  )
}

export default Sensorstatus;
