import React,{useEffect} from 'react'
import Typography from '@mui/material/Typography';
import { useDispatch, useSelector } from "react-redux";
import {setsensordata} from "../features/sensordata"
import io from "socket.io-client";
import { Gauge, gaugeClasses } from '@mui/x-charts/Gauge';


const socket=io.connect("http://localhost:4000");

function Sensorstatus() {

  const dispatch = useDispatch();
  const sensordata = useSelector(state => state.sensordata.value);
  
  useEffect(()=>{
    socket.on("Sensordata",(data)=>{
        dispatch(setsensordata(data)); 
    });
  });
  
  return (
    <div>
      <Typography variant="body2" color="text.secondary">
        Overhead Tank Float Switch     : {sensordata.OHT_Float}
      </Typography>
      <Typography variant="body2" color="text.secondary">
        Underground Tank Float Switch  : {sensordata.UGT_Float}
      </Typography>
      
      <Gauge
            value={100}
            startAngle={-110}
            endAngle={110}
            sx={{
                [`& .${gaugeClasses.valueText}`]: {
                fontSize: 40,
                transform: 'translate(0px, 0px)',
                },
            }}
            text={
                ({ value, valueMax }) => `${value} / ${valueMax}`
            }
      />

    </div>
  )
}

export default Sensorstatus;
