import React from 'react';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import axios from "axios";

const SERVER_URL ="http://localhost:4000";

function ControlledSwitches() {
  const [checked, setChecked] = React.useState(false);
  const [state, setState] = React.useState('OFF');
  const [captchaInput, setCaptchaInput] = React.useState('');
  const [captchaValid, setCaptchaValid] = React.useState(false);

  const expectedCaptcha = '12345'; 

  const handleChange = (event) => {
    const isChecked = event.target.checked;
    if (captchaValid) {
      setChecked(isChecked);
      setState(isChecked ? 'ON' : 'OFF');
      const data = {
        isChecked:isChecked
      }
      handleswitch(data);
      if (!isChecked) {
        setCaptchaValid(false);
        setCaptchaInput('');
      }
    }
  };
  
  const handleCaptchaChange = (event) => {
    setCaptchaInput(event.target.value);
  };

  const validateCaptcha = () => {
    if (captchaInput === expectedCaptcha) {
      setCaptchaValid(true);
    } else {
      setCaptchaValid(false);
      alert('Invalid Password. Please try again.');
    }
  };

  const handleswitch = async(data)=>{
    try{
      const response = await axios.post(`${SERVER_URL}/switch`,data,{headers: {
              'Content-Type': 'application/json'
             }
          });
      console.log(response);
    }catch (error) {
      console.error('There was an error making the POST request!', error);
    }
  }


  return (
    <Box>
      <TextField
        label="Enter Password to Turn ON"
        variant="outlined"
        value={captchaInput}
        onChange={handleCaptchaChange}
        disabled={captchaValid}
        type="password"
        fullWidth
        margin="normal"
      />
      <Button
        variant="contained"
        onClick={validateCaptcha}
        disabled={captchaValid}
        fullWidth
        sx={{ mb: 2 }}
      >
        Validate
      </Button>
      <FormControlLabel
        control={
          <Switch
            checked={checked}
            onChange={handleChange}
            color="warning"
            disabled={!captchaValid}
          />
        }
        label={state}
      />
    </Box>
  );
}

export default ControlledSwitches;
