import React from 'react';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import axios from "axios";

const SERVER_URL ="http://localhost:4000";

function ControlledSwitches() {
  const [checked, setChecked] = React.useState(false);
  const [state, setState] = React.useState('OFF');
  const [captchaInput, setCaptchaInput] = React.useState('');
  const [captchaValid, setCaptchaValid] = React.useState(false);

  const expectedCaptcha = '12345'; 

  const handleChange = (event) => {
    const isChecked = event.target.checked;
    if (captchaValid) {
      setChecked(isChecked);
      setState(isChecked ? 'ON' : 'OFF');
      const data ={
        isChecked:isChecked,
        state:state
      }
      handleswitch(data);
      if (!isChecked) {
        setCaptchaValid(false);
        setCaptchaInput('');
      }
    }
  };
  
  const handleCaptchaChange = (event) => {
    setCaptchaInput(event.target.value);
  };

  const validateCaptcha = () => {
    if (captchaInput === expectedCaptcha) {
      setCaptchaValid(true);
    } else {
      setCaptchaValid(false);
      alert('Invalid Password. Please try again.');
    }
  };

  function handleswitch(isChecked){
    fetch('https://api.example.com/data', {
      method: 'POST', // Specify the method
      headers: {
          'Content-Type': 'application/json' // Specify the content type
      },
      body: JSON.stringify(postData) // Convert the data to JSON string
  })
      .then(response => {
          if (!response.ok) {
              throw new Error('Network response was not ok');
          }
          return response.json(); // Parsing the JSON response
      })
      .then(data => {
          console.log(data); // Handle the data from the response
      })
      .catch(error => {
          console.error('There was a problem with the fetch operation:', error);
      });
  }


  return (
    <Box>
      <TextField
        label="Enter Password to Turn ON"
        variant="outlined"
        value={captchaInput}
        onChange={handleCaptchaChange}
        disabled={captchaValid}
        type="password"
        fullWidth
        margin="normal"
      />
      <Button
        variant="contained"
        onClick={validateCaptcha}
        disabled={captchaValid}
        fullWidth
        sx={{ mb: 2 }}
      >
        Validate
      </Button>
      <FormControlLabel
        control={
          <Switch
            checked={checked}
            onChange={handleChange}
            color="warning"
            disabled={!captchaValid}
          />
        }
        label={state}
      />
    </Box>
  );
}

export default ControlledSwitches;
